import React from "react";
import { BTextField } from "./FormStyles";
class Contact extends React.Component {
  state = { name: "", email: "", message: "" };
  handleChange(e) {
    this.setState({ [e.target.id]: e.target.value });
  }
  render() {
    return (
      <div className="main">
        <br /> <br /> <br /> <br />
        <br />
        <div className="colWithMargin">
          <p className="montserrat bold regBlueText underNegativeMargin">
            CONTACT US
          </p>
          <p className="redText xxlText">
            Feel free to drop by or shoot us an email
          </p>
        </div>
        <div className="rowWithMargin verticalPaddingM">
          <BTextField
            id="name"
            label="Name"
            variant="outlined"
            style={{ width: "100%" }}
            onChange={(e) => this.handleChange(e)}
            value={this.state.name}
          />
          <br />
          <br />
          <BTextField
            id="email"
            label="Email"
            variant="outlined"
            style={{ width: "100%" }}
            onChange={(e) => this.handleChange(e)}
            value={this.state.email}
          />
          <br />
          <br />
          <BTextField
            label="Your Message"
            placeholder="Your Message"
            variant="outlined"
            style={{ width: "100%" }}
            id="message"
            onChange={(e) => this.handleChange(e)}
            minRows={2}
            multiline
            maxRows={4}
            value={this.state.message}
          />
        </div>
      </div>
    );
  }
}

export default Contact;
