import React from "react";
import softwares from "./softwares.json";

class Software extends React.Component {
  state = {
    query: "",
    softwares: softwares.softwares,
  };
  componentDidMount() {}
  handleChange = (e) => {
    this.setState({ query: e.target.value });
  };
  render() {
    return (
      <div className="main">
        <div className="rowWithMargin verticalPaddingM col">
          <br />
          <br />
          <p className="montserrat bold regBlueText">AVAILABLE SOFTWARE</p>

          <p className="redText xxlText">
            Learn more about what software we offer
          </p>
          <input
            placeholder="Search our resources"
            className="searchInput"
            onChange={this.handleChange}
            value={this.state.query}
          ></input>
          <div className="rowM verticalPaddingM">
            <div className="triL">
              {this.state.softwares.map((software, softwareIndex) => {
                return (
                  <p
                    style={{
                      backgroundColor: this.state[
                        "s" + softwareIndex.toString() + "IsActive"
                      ]
                        ? "yellow"
                        : "transparent",
                    }}
                  ></p>
                );
              })}
            </div>
            <div className="tri">
              {this.state.softwares.map((software, softwareIndex) => {
                return <p></p>;
              })}
            </div>
            <div className="triR">
              {this.state.softwares.map((software, softwareIndex) => {
                return <p></p>;
              })}
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Software;
